import React, { Component } from "react";
import "./counter.css";

class PostQuote extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div>
        <div>
          <label>submit a quote:</label>
          <input
            type="text"
            className="input"
            label="submit a quote:"
            onChange={this.props.updateInputQuote}
          />
          {this.props.postedQuotes.map((quote, index) => (
            <p>{quote.title}</p>
          ))}
        </div>
        <button onClick={this.props.postQuote} className="button">
          POST
        </button>
      </div>
    );
  }
}

export default PostQuote;
