import React, { Component } from "react";
import "./counter.css";

class RecievedQuotes extends Component {
  constructor(props) {
    super(props);
  }

  getQuote = () => {
    fetch(`https://jsonplaceholder.typicode.com/todos/1`)
      .then(response => response.json())
      .then(result => {
        this.props.updatePostedQuotes(inputQuote);
        this.setState({ inputQuote: "" });
        this.setState(prevState => ({
          recievedQuotes: [result, ...prevState.recievedQuotes]
        }));
      });
  };

  render() {
    return (
      <div>
        <button onClick={this.getQuote} className="button">
          GET QUOTE
        </button>
        {this.props.recievedQuotes.map((quote, index) => (
          <p>{quote.title}</p>
        ))}
      </div>
    );
  }
}

export default RecievedQuotes;
