import React, { Component } from "react";
import GetQuote from "./GetQuote";
import PostQuote from "./PostQuote";
import "./counter.css";

class Counter extends Component {
  state = { recievedQuotes: [], postedQuotes: [], inputQuote: "", author: "" };

  getQuote = () => {
    fetch(`https://jsonplaceholder.typicode.com/todos/1`)
      .then(response => response.json())
      .then(result => {
        console.log(result);
        console.log(this.state.recievedQuotes);
        this.setState(prevState => ({
          recievedQuotes: [result, ...prevState.recievedQuotes]
        }));
      });
  };

  postQuote = event => {
    event.preventDefault();
    const inputQuote = {
      inputQuote: this.state.inputQuote,
      author: this.state.author
    };
    console.log(inputQuote);
    this.setState({ inputQuote: "" });
    this.setState(prevState => ({
      postedQuotes: [result, ...prevState.postedQuotes]
    }));
    // fetch("https://example.com/profile", {
    //   method: "POST", // or 'PUT'
    //   headers: {
    //     "Content-Type": "application/json"
    //   },
    //   body: JSON.stringify(inputQuote)
    // })
    //   .then(response => response.json())
    //   .then(data => {
    //     console.log("Success:", data);
    //   })
    //   .catch(error => {
    //     console.error("Error:", error);
    //   });
  };

  updateInputQuote = event => {
    let inputQuote = event.target.value;
    this.setState({ inputQuote });
    console.log(this.state.inputQuote);
  };

  render() {
    return (
      <div className="row">
        <div className="column">
          <GetQuote
            getQuote={this.getQuote}
            recievedQuotes={this.state.recievedQuotes}
          />
        </div>
        <div className="column">
          <PostQuote
            onChange={this.updateInputQuote}
            postQuote={this.postQuote}
            postedQuotes={this.state.postedQuotes}
          />
        </div>
      </div>
    );
  }
}

export default Counter;
