import React, { Component } from "react";
import "./counter.css";

class Counter extends Component {
  state = { recievedQuotes: [], inputQuote: "", author: "" };

  getQuote = () => {
    fetch(`https://jsonplaceholder.typicode.com/todos/1`)
      .then(response => response.json())
      .then(result => {
        console.log(result);
        console.log(this.state.value);
        this.setState(prevState => ({
          value: [result, ...prevState.value]
        }));
      });
  };

  putQuote = () => {};

  putQuote = event => {
    event.preventDefault();
    const inputQuote = {
      inputQuote: this.state.inputQuote,
      author: this.state.author
    };

    fetch("https://example.com/profile", {
      method: "POST", // or 'PUT'
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(data)
    })
      .then(response => response.json())
      .then(data => {
        console.log("Success:", data);
      })
      .catch(error => {
        console.error("Error:", error);
      });
  };

  render() {
    return (
      <div>
        <button onClick={this.getQuote} className="button">
          GET
        </button>
        {/* <button onClick={this.putQuote}>PUT</button> */}
        {this.state.value.map((value, index) => (
          <p>{value.title}</p>
        ))}
        <form onSubmit={this.putQuote}>
          <p>submit a quote:</p>
          <input type="text" className="input" onChange={this.updateQuote} />
          {/* <input type="submit" className="button" /> */}
        </form>
      </div>
    );
  }
}

export default Counter;
