import React, { Component } from "react";
import GetQuote from "./GetQuote";
import PostQuote from "./PostQuote";
import "./counter.css";

class Counter extends Component {
  state = { recievedQuotes: [], postedQuotes: [] }; //, inputQuote: "", author: "" };

  getQuote = () => {
    fetch(`https://jsonplaceholder.typicode.com/todos/1`)
      .then(response => response.json())
      .then(result => {
        console.log(result);
        console.log(this.state.recievedQuotes);
        this.setState(prevState => ({
          recievedQuotes: [result, ...prevState.recievedQuotes]
        }));
      });
  };

  updatePostedQuotes = inputQuote => {
    this.setState(prevState => ({
      postedQuotes: [inputQuote, ...prevState.postedQuotes]
    }));
  };

  render() {
    return (
      <div className="row">
        <div className="column">
          <GetQuote
            getQuote={this.getQuote}
            recievedQuotes={this.state.recievedQuotes}
          />
        </div>
        <div className="column">
          <PostQuote
            updatePostedQuotes={this.postQuote}
            postedQuotes={this.state.postedQuotes}
          />
        </div>
      </div>
    );
  }
}

export default Counter;
