import React, { Component } from "react";
import "./counter.css";

class PostQuote extends Component {
  constructor(props) {
    super(props);
  }
  state = { inputQuote: "" };

  postQuote = event => {
    event.preventDefault();
    const inputQuote = this.state.inputQuote;

    console.log(inputQuote);
    this.setState(prevState => ({
      postedQuotes: [inputQuote, ...prevState.postedQuotes]
    }));
    this.setState({ inputQuote: "" });
    // fetch("https://example.com/profile", {
    //   method: "POST", // or 'PUT'
    //   headers: {
    //     "Content-Type": "application/json"
    //   },
    //   body: JSON.stringify(inputQuote)
    // })
    //   .then(response => response.json())
    //   .then(data => {
    //     console.log("Success:", data);
    //   })
    //   .catch(error => {
    //     console.error("Error:", error);
    //   });
  };

  onInputChange = event => {
    let inputQuote = event.target.value;
    this.setState({ inputQuote });
    console.log(this.state.inputQuote);
  };

  render() {
    return (
      <div>
        <button onClick={this.postQuote} className="button">
          POST
        </button>
        <div>
          <label>submit a quote:</label>
          <input
            type="text"
            className="input"
            onChange={this.onInputChange}
            value={this.state.inputQuote}
          />
          {this.props.postedQuotes.map((quote, index) => (
            <p>{quote.quote}</p>
          ))}
        </div>
      </div>
    );
  }
}

export default PostQuote;
